INSERT INTO `review` (`id`, `User_Name`, `Dianping_Date`, `Company_Name`, `Impression`, `Comment`, `I1`, `I2`, `I3`, `I4`) VALUES
(0, 'lfyxyxy', '2016-02-29', '大拇哥财富', '还不错,还算透明,收益低,', '投资人在选择P2G平台时，也要多观察、多了解、多用心.平台挺透明的，很真实', 3, 5, 4, 4),
(1, 'zhangwenone1', '2016-02-29', '点牛金融', '透明,服务好,有发展前景,', '对点牛的感觉还是不错，希望它能越办越好，现在看它的B轮', 3, 4, 5, 5),
(2, 'powpow', '2016-02-29', '多赢', '老板牛,还不错,标难抢,', '挺靠谱的，在投，参加过一次投资者见面会，现场验标，有一个法律专业的投资者现场抓着验了一个多小时，没发现问题，这么较真的童鞋也是挺难得的，也就是因为这个，现场很多人都折服了，有投资之人说，现在多赢变成了他投资P2P平台的一个标准了，比他收益高的怕不安全，比他收益低的又不甘心，反正钱在这很放心。', 2, 4, 4, 5),
(3, '宝山', '2016-02-29', '54贷客', '标难抢,提现快,还算透明,', '最近标真是难抢，开着自动投标还要手动投才有可能投上。好在自动投标的排队顺序已经出来了，投资返现活动就快结束了，也不知道三月有什么新活动？', 4, 5, 4, 5),
(4, '8888648222', '2016-02-29', '诺诺镑客', '还不错,', '不错的平台，回款及提现超快，只是年前的工商注册异常解决了没有？', 5, 5, 5, 5),
(5, '倪惜文', '2016-02-29', '医购贷', '还不错,提现快,', '建议：信息再透明一点，实缴资金再多点，然后数据要和之家对接，就这些', 4, 4, 4, 5),
(6, '陌上花静开', '2016-02-29', '富金利', '推荐,', '投资半年了，觉得网站整体做的还是可以的，希望活动的力度可以再大一些些，我不会介意的!!哈哈哈', 4, 4, 5, 5),
(7, 'liuchu', '2016-02-29', '360迪迪贷', '标难抢,还不错,', '感觉还比较稳健，尤其是平台借款人的信息描述很清楚，最喜欢的的是他们3个月的房产标，挺不错的，就是标的有点难抢。', 3, 5, 4, 4),
(8, '房蛋', '2016-02-29', '医购贷', '老板牛,没人气,还算透明,', '确实是小而美的平台，希望越来越好，提现速度很快，还帮我们垫付了手续费，提个建议：网站有待优化', 4, 4, 4, 4),
(9, '书生香评', '2016-02-29', '握握贷', '资金安全,背景强大,提现快,', '朋友介绍的这家平台，观望了一段时间发现还可以。有国资参股，股权质押，还是蛮靠谱的。其他方面还在观察中，希望平台越办越好。', 3, 4, 4, 3),
(10, 'xiaxianu', '2016-02-29', '德众金融', '还不错,标难抢,', '我在平台的投资利息逾期了，但是我觉得并不像其他人口中说的那么可怕，平台有逾期是正常的，但是希望可以尽快收回，对平台有信心，也会耐心等待回款的，希望你们妥善处理。', 5, 5, 4, 3),
(11, '178169387', '2016-02-29', '海星宝理财', '使用便捷,服务专业很好,很透明,', '朋友推荐的平台，投资也有一段时间了，提现速度和收益真的是棒棒的，而且资金有新浪支付的托管，放心了不少。', 5, 5, 5, 5),
(12, 'chen12', '2016-02-29', '世纪贷', '老板牛,还不错,', '平台已经上线一年多，3月标的年利率还不错，全部是质押车辆项目，提现也非常快，推荐大家有机会可以尝试下', 4, 5, 5, 5),
(13, '流年映照青春', '2016-02-29', '钱盆网', '信息透明,服务周到,', '希望除了大额资金充值，其他功能手机APP上都能实现。', 5, 4, 4, 5),
(14, '挑战雪舞银河', '2016-02-29', '搜易贷', '还不错,', ' 对理财一直很有兴趣，也愿意尝试网络理财方式，投资搜易贷以来，回款很快很准时，每次我都把工资放在这里', 2, 5, 5, 5),
(15, 'woshihonghushui', '2016-02-29', '安贷宝', '还不错,', '平台运营平稳，希望越来越hao', 4, 5, 4, 5),
(16, '机智的蓝波桑', '2016-02-29', '金票通', '适合新人,项目透明,活动棒,', '金票通和我的南京合作啦！新手标还送电影票！！宝宝桑心了。。。为嘛我投的时候不送。。。新手标还14%收益呢。。。话说最近还有啥活动啊~~活动的时候买产品超级划算啊~APP改版之后活期变成1元起投了，超级适合新手~大家可以去尝试下哈2333', 3, 5, 5, 5),
(17, '达人520', '2016-02-29', '格林易贷', '还算透明,还不错,三方审计,', '刚刚看了公告说格林易贷顺利通过联交所上市公司2015年度审计公告，应该来说还不错，重大利好！', 3, 3, 3, 4),
(18, '凯特琳', '2016-02-29', '月光宝盒', '零费用,提现快,老板牛,', '那天在今日头条上看到月光宝盒理财，说是有存管，而且资产端是信托拆分，很有想法，今天就过来了解一下，好像还真的不错，可以试一试啊。', 3, 3, 3, 3),
(19, 'jfkl45', '2016-02-29', '金投手', '老板牛,还不错,', '我喜欢短期的，像金投手这样的都是半年以内的短期投资，特别适合我，继续投资，希望越来越好。', 5, 5, 5, 5),
(20, 'wdzj15856536619', '2016-02-29', '快快贷', '还不错,', '标的期限较短，利息还可以，回款还很准时；活期利息高，提现方便到账快。', 3, 5, 4, 4),
(21, '18686687375', '2016-02-29', '快速贷', '涅槃重生牛逼,股东多实力强,', '2014年我在这遭遇限提，那一刻确实很苦恼，但是现在看到朱飞机的努力，又成立集团收购新三板、建立基金公司、理财保险公司，并且重新涅槃重生了，还有上千个股东支持~我放心！坦诚相待必将化险为夷、信任支持定能共展未来，现在我继续加大投资，用事实证明了我的眼光！加油~飞机↗', 3, 5, 5, 5),
(22, 'ahhhh2008', '2016-02-29', '铜掌柜', '还不错,', '这平台还是不错，看着它成长，我慢慢也越投越多，希望越做越好！', 3, 5, 4, 5),
(23, '随便投投', '2016-02-29', '翼龙贷', '不透明,坑爹,平台不负责任,', '投资散标，一年逾期4次！问客服，结果回答的给你绕老绕去没有个明确答案，虽然看着收益高，但还不是有手续费，坑爹！大家千万不要上当了，什么联想控股的，一点实际的意义都没有！！！', 1, 3, 2, 1),
(24, '随便投投', '2016-02-29', '爱钱进', '还不错,老板牛,服务不错,', '爱钱进算是投资比较早的平台，最初也是试试，后来加了QQ群，就更加放心投资了（老板不错  客服也很热情，总体挺好。虽然也有降息吧，但安全就好。现在提现时间又缩短了，感觉还是比较看重客户体验的，就是活动是不是有点创意，别老是那几个&hellip;&hellip;', 3, 4, 3, 4),
(25, 'goodmanqiu', '2016-02-29', '易贷网', '老板牛,债权转让方便,', '希望任总和易贷高层加强政策的掌握。和政策比，跑的太快会容易出事，跑的太慢就会落后。', 2, 5, 4, 5),
(26, '8886490431', '2016-02-29', '民贷天下', '资金安全,集团实力雄厚,网站流畅,', '讲信誉，投资有安全保障，投资到期当天都能返款，到期资金自动到账还有短信提示，服务态度也很棒，值得信赖，体现速度快，利率也比银行高很多', 5, 5, 4, 5),
(27, 'gagagfgfda', '2016-02-29', '民贷天下', '还不错,', '做理财的话，个人认为还可以，收益这个还行，安全还算有保障，公司背景实力挺好，服务态度很端正，风控团队来自银行，在网上和新闻都有报道，可以关注', 4, 5, 5, 5),
(28, '口乞女乃', '2016-02-29', '妥妥当', '老板牛,', '工商查询确实国资，比较安全，小小投资了几万', 3, 5, 5, 5),
(29, '草原上种稻草', '2016-02-29', '58money', '还不错,零费用,', '年前朋友推荐了，所以观察了半个月，年化收益算不错，活动奖励高，感觉标的速度和投资记录都还算正常，然后去注册了一下，流程还好，先投个两万，，继续边观察边投投看吧，，', 4, 5, 5, 5),
(30, '3188569807', '2016-02-29', '民贷天下', '还算透明,', '还行吧，安全程度较好。民贷天下的背景比较强大，实力雄厚，但是总体感觉一般，至于网站的设计，现在改版后比之前好了些。在安全保障方面风控做得好，有专业的银行级风控水平，有风险备用金', 5, 3, 5, 5),
(31, 'jun0101', '2016-02-29', '启道金融', '零逾期,', '现在新疆的P2P网贷也有很多，但是能让我一直坚持选择的也只有启道金融，每次还款都准时，不逾期，平台标的也不错。', 5, 5, 5, 4),
(32, '小魔术', '2016-02-29', '量子金融', '标难抢,零费用,提现快,', '投过，标挺难抢的，标的额度太小了，几个人就抢完了，基本上一天两个标，年化15.8还行吧，提现没提过，不过客服说基本当天或隔天就到了，质押的车辆信息公示挺全面的，还有摄像头监控，总体而言还不错吧', 3, 5, 3, 3),
(33, '土地公', '2016-02-29', '易贷网', '还不错,标难抢,还算透明,', '易贷能有今天，我看任总好像不是个能说会道的主而倒是有点像个实干家；我个人认为说得好就是不如做得好......实践是检验真理的唯一标准......路漫漫其修远兮吾将上下而求索......只有一直走到路的尽头，都能经得住历史和法律的检验，方知易贷得的是金杯、银杯还是口碑！', 3, 4, 5, 5),
(34, 'vc一点点', '2016-02-29', '格林易贷', '老板牛,', '平台挺好，提现快，各方面都有保障。', 5, 5, 5, 5),
(35, '庄淳', '2016-02-29', '玖盈贷', '老板牛,还不错,提现快,', '我是在新年之后一次无意的授索中，发现了玖盈贷，一开始对它存有怀疑，到后来的了解和信任，收益高、服务周到，还是很强的实力背景的、值得信赖，而且我会继续支持玖盈贷的！', 5, 5, 5, 5),
(36, '宋宋宋sss', '2016-02-29', '广州e贷', '提现快,上市公司入股,实力雄厚,', '投过几个平台，感觉广州e贷还是不错的，具有上市公司入股，实力背景比较雄厚，就提现这方面它们做得是挺好的，提现速度快，提现费用还全免，整体的投资体验度算是投的几个平台里面比最好的了。其他方面吧还在观察中，希望做得越来越完善了，毕竟只有平台发展得越好对我们投资者也越有利。', 5, 5, 5, 5),
(37, '丨风月无边丨', '2016-02-28', '陆金所', '老板牛,提现快,', '可以点开图片扫码了解具体操作，5000十四天获58年化收益率高达30%', 5, 5, 5, 5),
(38, '45度仰望星空', '2016-02-28', '广州e贷', '提现快,零费用,还不错,', '朋友推荐的，说认识他们老板，可以排除自融的嫌疑。目前感觉是体验还不错，提现速度赞一个。', 5, 5, 4, 4),
(39, '我是幸运星', '2016-02-28', '有利网', '老板牛,还不错,', '有利网是我精挑细选才下叉的，哈哈哈，目前看来很不错，感觉投友们也像一个大家庭一样其乐融融，不错，继续支持', 2, 2, 4, 4),
(40, '我是幸运星', '2016-02-28', '任我贷', '标难抢,没人气,债权转让方便,', '本来对P2P行业持一种怀疑态度的，但是最终还是投了，因为身边好多兄弟伙都在投，所以跟着他们一起试试水，投了两家大的平台也投了两家像任我贷这样的小平台，都说鸡蛋不要放在同一个篮子里嘛，我也怕栽水撒。就目前来看任我贷还比较平稳，不浮夸，符合哥的调性，哈哈，继续支持起~希望在这里找到投友一起交流~', 3, 3, 3, 4),
(41, '一生足矣123', '2016-02-28', '财路通', '老板牛,还不错,', '招财猪是我买的最久的产品了，现在我也开始买摇钱树了！', 5, 5, 5, 5),
(42, '8887642816', '2016-02-28', '钱来网', '平台不负责任,', '推荐奖励2.18提现，到2.18还没有到账', 1, 3, 1, 3),
(43, '8886904768', '2016-02-28', '爱钱进', '不透明,', '我感觉这个平台怎么计算利息的，13.49%是怎么计算，我咋个看不出来，一年的也才11%啊，是不是我没有找到啊。', 3, 4, 3, 3),
(44, 'yxg3240', '2016-02-28', '玖富理财', '还不错,提现快,零费用,', '从安全性、权威性、利率性、费用性、门槛性等方面综合评价不错，也是全国用户数量超过千万的全国4大网站之一，平均每天增加10万用户，企业信誉很高', 3, 5, 4, 4),
(45, '三浦', '2016-02-28', '民贷天下', '老板牛,信誉好,', '国资背景平台，利率中等偏高，已经不错了，关注了很久，最近开始投了几笔，还款提现都及时，客服专业细心，网站简洁方便，不错。', 4, 5, 5, 5),
(46, 'wrg3830', '2016-02-28', '德众金融', '无语,', '2月22日，付息的日子，有三笔利息未付，这个借款人连利息都付不起，本金岂不更够呛？一个号称零坏账的国资平台尚且这样，遑论其他平台？平台所称的第一道安全保障&ldquo;严把风控关&rdquo;不攻自破。', 5, 5, 3, 3),
(47, 'wdzj13951898101', '2016-02-28', '小牛在线', 'nan', '小牛在线很不错啊！在p2p网络平台中，算蛮好的！收益算蛮高的了，灵活性也好，提现速度也还可以，各人选择!', 5, 5, 5, 5),
(48, '优雅从容', '2016-02-28', '搜易贷', '老板牛,', ' 搜易贷资金托管这一块确实是对于网贷投资人来讲是一个突破，安全以及收益稳定很不错。期待搜易贷以后能更好，这样就可以实现平台、借款者、投资者三方共赢了。', 2, 5, 4, 5),
(49, 'yd2016', '2016-02-28', '广州e贷', '老板牛,还算透明,提现快,', '客服还款时间不是统一的一个时间点，标的真实可靠。体现也是没话说，秒到。总体来说，在广州e贷平台投资还是比较放心的。股东实力雄厚，参加过他们的投资者见面会，方总，朱总也是很有想法的人。', 5, 4, 5, 5),
(50, '8884462210', '2016-02-28', '陆金所', '老板牛,', '投陆金所就是因为平台的背景，安全，不过收益有些低。', 3, 4, 4, 4),
(51, '8882504034', '2016-02-28', '广州e贷', '老板牛,零费用,', '从投之家推荐开始投资的，之后也在网上查了一下相关的信息，媒体报道很多，在媒体报道中，广州e贷面对行业问题从不遮蔽也不回避，个人认为，有底气的公司应该是这个样子。投资后感觉这个平台也很务实，T+0提现，投资即计息这些小方面都做的很好。赞一个，希望能一直保持。', 5, 5, 4, 4),
(52, 'hhw7325976', '2016-02-28', '信融财富', '还不错,还算透明,', '前几天提现都正常，今天怎么登录不了呀？？？', 3, 5, 4, 5),
(53, '猫-cc', '2016-02-28', '步步盈', '安全,正规,还算透明,', '关注平台1年多，一直小额投资，感觉很不错，体现速度很快，还是可以推荐的，准备继续观察一段时间，就可以大额投资了', 3, 4, 5, 5),
(54, '8884462210', '2016-02-28', '互贷网', '还不错,标难抢,', '收益比陆金所的高，还款正常，就是有些难抢，今天登录进去都是满标状态。', 3, 4, 4, 4),
(55, '小龙虾.', '2016-02-28', '前海广深发', '老板牛,标难抢,', '平台稳定、可以投资，各种活动，各种投资。', 5, 5, 5, 5),
(56, '乔晴', '2016-02-28', '沃时贷', '无语,坑爹,高危,', '亲们不要只看注册资金，要多留一下实缴资金，毕竟后者才是真金白银的钱，注册资金只是一句空话，吹嘘的多越危险。许多上亿注册资金的实缴只有100万啊！沃时贷实缴为0啊！！！', 3, 3, 1, 4),
(57, 'quicksand', '2016-02-27', '阿朋贷', '还不错,还算透明,', '资金进出感觉还不错，到账比较及时，就是页面设计比较Low,跟公司不太匹配。短期标收益不高不低，比较吸引人。', 3, 5, 4, 3),
(58, '何毅0326', '2016-02-27', '安捷财富', '无语,服务差,', '在平台交流群里提意见被踢了，我也是醉了！难道只能问一些出个FAQ就能涵盖的问题？客服雯雯对待小额投资人的态度不敢恭维', 3, 5, 3, 1),
(59, 'kelei_zhu', '2016-02-27', '快速贷', '老板牛,', '快速贷是恒汇旗下子公司，恒汇集团旗下已经收购一个三板公司，老板控股一个三板公司。', 3, 4, 5, 1),
