INSERT INTO `review` (`id`, `User_Name`, `Dianping_Date`, `Company_Name`, `Impression`, `Comment`, `I1`, `I2`, `I3`, `I4`) VALUES
(7563, 'jiyang163', '2015-09-14', '小存折', '信息透明,安全,客服热情细心,', '小存折平台披露的信息很完整，基本上企业的各类信息都能清晰可见，利率在安全界限内，适合投资，客服的美眉声音甜美，专业知识也很强，投资还有积分可以拿，换成红包可继续投资，最后都能无条件提现，等于多收益1%，值得推荐的平台，赞一个。', 3, 5, 5, 5),
(7564, 'szinzaghi', '2015-09-14', '沃时贷', '还不错,提现快,', '沃时贷我已经投资半年多了，体验相当不赖。专属客服小周也不错。利息水平也很满意，只是不要再降息了哦。沃时贷加油，希望越办越好，不要让投资人失望。比起那个什么随便踢投资人的月月舒（dai）强太多了。', 3, 4, 5, 5),
(7565, 'szinzaghi', '2015-09-14', '月月贷', '无语,不负责任的平台,服务差,', '   我在月月贷已经投资快一年了，一直都还比较顺利。直到昨天我在官群发了个某东红包链接，客服HK一声不吭就直接把我踢出官群。由于客服号设置屏蔽我无法发起临时会话，只能通过好友申请说明情况。但客服直接把我拒绝，拒绝理由里只说了一句群里禁发无关链接然后就没有任何回应了。', 2, 2, 1, 1),
(7566, 'xiaoqingchong', '2015-09-14', '你我贷', '坑爹,', '见有18%的新手标，充值了一万，没想到不能投资！真不明白既然不让投资干嘛设置充值功能，骗人！钱转出再也不碰这垃圾平台', 1, 1, 1, 1),
(7567, '但愿永不踩雷a', '2015-09-14', '元贷通', '无语,坑爹,没人气,', '我真不明白这种网站能活到现在的原因，大户早就撤了 现在又搞了这么大的活动估计就是来忽悠小户接盘。毫无优势的一个网站，客服服务烂到渣  我随便一质疑就故意关掉窗口。反正我已经撤出了，你们自己看着办吧！', 2, 2, 1, 1),
(7568, 'syp', '2015-09-14', '财加', '不透明,', '其实算是拿了风投里面的散标收益还算不错的，能到18%，不过优投宝收益太低了，散标逾期不是马上垫付的，不过能垫付其实就在我可接受范围内吧。很多人吐槽系统和体验感什么的，我也不多说了。平台不出问题就好了。', 3, 5, 4, 4),
(7569, 'lances', '2015-09-14', '开心贷', '不透明,', '体验一般般。', 2, 3, 3, 3),
(7570, 'lances', '2015-09-14', '普天贷', '老板牛,还不错,', '一般，还行。', 3, 3, 3, 3),
(7571, 'ZXB009', '2015-09-14', '博金贷', '老板牛,还不错,', '的确是个好平台。不过隐性风险不小。完全依赖小贷公司，债权由其转让，逾期债权由其回收，小贷公司把风险全部转移给平台了。万一小贷公司发生经营或者信用危机，遭受损失的不只是出借人，平台自身更会遭到致命重创。因此平台务必要有自己的风险准备金，以应小贷公司不能如期回收债权之急。', 2, 4, 4, 4),
(7572, '小伙子儿', '2015-09-14', '手投网', '标难抢,还算透明,', '通过手机微信注册的，得了20块钱红包，新手专享标太难抢，利率一般，不属于高的，提现还可以，当天到账', 4, 5, 4, 4),
(7573, '小伙子儿', '2015-09-14', '有利网', '不透明,老板牛,', '就是名气大，但网站体验真心一般', 3, 4, 3, 3),
(7574, 'lingzhi', '2015-09-14', '积木盒子', '还不错,', '除了利息低一点其他都挺好的，我都是分散投资的，在Hi投吧我也投了，因为利息高有15%以上，而且他们做全实物抵质押的，所以还比较放心，希望这样放心靠谱的平台越来越多就好', 3, 4, 4, 4),
(7575, 'zeifox', '2015-09-14', '拓道金服', '还不错,还算透明,提现快,', '总的来说还不错的平台，比较透明，只做车贷，风险小，现在活动多，年化还不错，希望以后也可以多点活动。', 3, 5, 4, 5),
(7576, 'free998', '2015-09-14', '理想宝', '老板牛,还不错,', '活动都很新颖有活力！官网页面终于改版了！提现速度快！', 4, 5, 4, 4),
(7577, '达令~', '2015-09-14', '六六投资', '坑爹,不透明,无语,', '我在他们平台投资一个月的标，然后听朋友说他们要雷了，到期之后我申请提现，几天过去之后还无法提现', 1, 1, 1, 1),
(7578, '他是心脏', '2015-09-14', '钱盆网', '提现快,还不错,老板牛,', '说实话，我投过很多平台，也踩雷过，现在投到钱盆网，从哪个方面都感觉钱盆网这个平台不错。', 5, 5, 5, 5),
(7579, '青一只', '2015-09-14', '微贷网', '还不错,保全性高,', '今天有笔投资到期了，到账很及时呀~赞一个，平台的安全可靠性真的不错~其他一些小平台真心不敢去尝试，不能一味的注重年利率，但是还是找一些稳定安全的来得好，', 4, 5, 5, 5),
(7580, '沉鱼', '2015-09-14', '网利宝', '老板牛,', '就目前来讲这个平台是我投过的所有平台最好的。收益高，回款迅速，而且通道支付费用也给用户免了，经常做力度很大的活动，愿平台越来越好', 3, 4, 4, 4),
(7581, 'hzpwn8117', '2015-09-14', '快速贷', '老板牛,', '虽然平台出现问题 但是他们上至老板下至客服无人在推脱，有困难不怕，怕的是不去解决困难，或者隐藏困难。从出问题到现在 我依然还是快速贷的投资者。比起那些出问题后平台就一句话没钱，比起那些还没出现问题，但是在投资人心目中没有任何实质保障的平台安全几百倍。', 3, 4, 4, 4),
(7582, '紫光1938', '2015-09-14', '今日捷财', '老板牛,还算透明,还不错,', '朋友推荐去的，利息还不错，风险相对要低一些，用了一段时间，还比较稳定。', 3, 5, 4, 5),
(7583, '双椒鱼', '2015-09-14', '迅捷贷', '还不错,', '在迅捷贷投资了一段时间，总体感觉还好，每次标到了。会有通知。利息收益了多少，也会有短信通知。整体感觉还不错！', 5, 5, 5, 5),
(7584, 'limeizhen', '2015-09-14', '丰益贷', '还算透明,提现快,零费用,', '体验感觉还不错哦，操作简单，提现也很快。', 3, 4, 4, 4),
(7585, 'hu464566040', '2015-09-14', '致富贷', '还不错,还算透明,提现快,', '投了1000元，用了50元代金券。总体还算不错。新平台，体验度有待提供。', 3, 4, 5, 5),
(7586, 'zhangzhangzhang', '2015-09-14', '摇财树', '还不错,没人气,零费用,', '还不错，不知道怎么说见仁见智吧，个人体验网站上一般，信得过客服', 4, 4, 5, 5),
(7587, 'szxhxt', '2015-09-14', '融易信', '不透明,', '无辜将我提出群', 1, 1, 1, 1),
(7588, 'anhrty', '2015-09-14', '宜人贷', '诈骗平台,', '这个是诈骗平台，已经被报道资金链断裂了，应该不久就雷了，所谓的排名神马的，完全不靠谱', 1, 1, 1, 1),
(7589, '你好再见明天', '2015-09-14', '投宝金融', '老板牛,还不错,还算透明,', '国资背景，活宝的利息确实挺高的，公司注册资金5000万确实牛叉，非常不错的平台。值得为大家推荐。', 5, 5, 5, 5),
(7590, 'Lemon树下的阳光', '2015-09-14', '币丰所', '还不错,', '个人觉得还不错，服务态度也很好，值得推荐一下', 5, 5, 5, 5),
(7591, 'yy341905539', '2015-09-13', '石投金融', '还不错,还算透明,', '感觉客服服务挺到位的。之前有操作的小问题，很耐心且很快的给我解决了。收益方面还不错，最主要资金到账快。投了一个月的标试了一下，还挺不错。', 4, 4, 4, 5),
(7592, 'xiaoqingchong', '2015-09-13', '可溯贷', '还不错,', '刘栋COO毕业于北京金融大学金融系，曾分别就职于银河证券杭州分公司、浦发银行杭州分行，从11年开始投资行业自主创业，2014年与合伙人共同创建互联网金融平台-可溯贷。这是高管介绍。可溯贷真的是国资吗？', 3, 5, 5, 4),
(7593, 'wang_jolin2002', '2015-09-13', '宜人贷', '不透明,无语,坑爹,', '网站提现困难，本想先投1000元试试，结果提现时审核失败，客服说是第三方支付问题，支支吾吾也不知道怎么回事，一再让我重新申请再试。这次体验感觉应该有资金池或虚假操作，充值几秒钟就搞定，提现最少要过2道审核关+等待3天，这种责权不对等的状况下，钱放在里面不踏实，这种平台实不推荐。', 1, 1, 1, 1),
(7594, '迷失红尘', '2015-09-13', '招商贷', '提现快,', '怎么证明实缴资本是五千万，上市公司中天城投控股？', 4, 4, 3, 3),
(7595, 'lanpihu', '2015-09-13', '石投金融', '还不错,还算透明,', '一直用他们家投资，收益挺高的，标也挺多，回款也挺及时，详细看了他们的官网，风控做得很好，还很安全，我很放心。', 3, 5, 5, 5),
(7596, '蒲公英的约定90', '2015-09-13', '金融工场', '还不错,还算透明,', '非常不错的一个平台，，大赞！！', 3, 5, 4, 4),
(7597, '蒲公英的约定90', '2015-09-13', '投哪网', '还不错,标难抢,', '对投哪网一直很满意在各方面，唯一不满意的就是投不到标，，唉。。。。。。', 3, 2, 5, 5),
(7598, '江小小小芯', '2015-09-13', '众可贷', '还不错,', '小而美的平台，从发的标可以看经营团队很专业，收益也还不错，是很本分认真做事的平台，投资这样的平台放心，希望平台好好坚守底线，越做越强！', 4, 5, 5, 5),
(7599, 'libobo', '2015-09-13', '合拍在线', '无语,', '就是个喜欢装逼的公司，喜欢耍些小技俩，又想要装大爷，又想不兑现承诺，投资时不看清楚条件，就会被绕进去，让你浪费了大把时间，又得不到看到的收益；坚绝不再投。', 2, 2, 2, 3),
(7600, 'qq414637369', '2015-09-13', '人人聚财', '还不错,', '我就想说你们搞这个体验金有啥用啊 ，过期了你们说了算，利率你们说了算，白送8%的年化7天，我都不知道我能投哪儿？标的是啥？客服问了半天也是估计在看帮助中心吧，周末在家闲的人挺多的，建议好好培训一下你们客服，要么就别出一堆让人回答不上来的产品，醉醉的哈哈哈 ，已经试水了。你们运营好好做啊。', 3, 3, 3, 3),
(7601, 'louistly', '2015-09-13', '财加', '没人气,不透明,', 'StarVC 到底投入多少啊？？怎么没个数呢？？', 2, 4, 4, 3),
(7602, 'ylxgpyl', '2015-09-13', '启道金融', '不透明,', '到低是民营企业还是国资，人气太低，谨慎', 2, 5, 2, 3),
